import React, { useState } from 'react'
import './Styles.css'
import Envelope from '../assets/envelope-solid.svg'
import Lock from '../assets/lock-solid.svg'
import Popup from './Popup'
import validator from 'validator'

const LeftContainer = () => {
  const [mail, setMail] = useState('')
  const [password, setPassword] = useState('')
  const [retypePassword, setretypePassword] = useState('')
  const [error, setError] = useState('')
  const [showPopup, setshowPopup] = useState(false)

  const handleClick = () => {
    if (!mail || !password || !retypePassword) {
      setError('Please enter all the fields')
      setshowPopup(true)
    } else if (!validator.isEmail(mail)) {
      setError('Please enter valid mail address ')
      setshowPopup(true)
    } else if (!validator.isStrongPassword(password)) {
      setError('Please enter strong password')
      setshowPopup(true)
    } else if (password !== retypePassword) {
      setError('password do not match')
    } else {
      alert(`clicked ${mail}`)
    }
  }
  return (
    <div className="left-container">
      <header>
        <h1>
          <span>Jo</span>in Us
        </h1>
      </header>
      <div className="email-container">
        <img src={Envelope} alt="email" width="20px" height="20px" />
        <input
          placeholder="Enter your email"
          type="email"
          value={mail}
          onChange={(e) => setMail(e.target.value)}
          required
        />
      </div>
      <div className="email-container">
        <img src={Lock} alt="password" width="20px" height="20px" />
        <input
          placeholder="Enter your password"
          type="password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          minLength="8"
        />
      </div>
      <div className="email-container">
        <img src={Lock} alt="password" width="20px" height="20px" />
        <input
          placeholder="Retype your password"
          type="password"
          minLength="8"
          value={retypePassword}
          onChange={(e) => setretypePassword(e.target.value)}
          required
        />
      </div>
      {error && <Popup errorMsg={error} />}
      <button onClick={handleClick}>Join</button>
      <p>
        Already have an account?
        <a href="#login">Login</a>
      </p>
    </div>
  )
}

export default LeftContainer
